import React from 'react';

function EmailList({ emails, responses, onSelectEmail, selectedEmail }) {
  return (
    <ul style={{ listStyle: 'none', padding: 0, background: '#fff', borderRadius: '5px', boxShadow: '0 2px 5px rgba(0,0,0,0.1)' }}>
      {emails.map(email => {
        const response = responses.find(r => r.emailId === email.id);
        const isSelected = selectedEmail && selectedEmail.id === email.id;
        return (
          <li
            key={email.id}
            onClick={() => onSelectEmail(email)}
            style={{
              padding: '15px',
              borderBottom: '1px solid #eee',
              background: isSelected ? '#e8f0fe' : response ? (response.isCorrect ? '#e6ffe6' : '#ffe6e6') : '#fff',
              cursor: 'pointer',
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center'
            }}
          >
            <div>
              <strong>{email.sender}</strong>
              <p style={{ margin: '5px 0', color: '#555' }}>{email.subject}</p>
            </div>
            <span style={{ color: '#888', fontSize: '0.9rem' }}>{email.date}</span>
          </li>
        );
      })}
    </ul>
  );
}

export default EmailList;
