import React from 'react';
import { Link } from 'react-router-dom';

function Results({ location }) {
  const responses = location.state?.responses || [];
  const score = responses.filter(r => r.isCorrect).length * 10;
  const total = responses.length * 10;

  return (
    <div style={{ padding: '20px', textAlign: 'center' }}>
      <h1>Resultados</h1>
      <p>Puntuaci√≥n: {score}/{total}</p>
      <p>Consejo: Revisa siempre el dominio del remitente antes de hacer clic en enlaces.</p>
      <Link to="/">Volver al inicio</Link>
    </div>
  );
}

export default Results;
