import React from 'react';
import { Link } from 'react-router-dom';

function Results({ location }) {
  const responses = location.state?.responses || [];
  const score = responses.filter(r => r.isCorrect).length * 10;
  const total = responses.length * 10;

  return (
    <div className="container">
      <h1>Resultados</h1>
      <p style={{ fontSize: '1.5rem' }}>Puntuaci√≥n: <strong>{score}/{total}</strong></p>
      <p style={{ fontStyle: 'italic', color: '#555' }}>
        Consejo: Revisa siempre el dominio del remitente antes de hacer clic en enlaces.
      </p>
      <Link to="/">
        <button>Volver al inicio</button>
      </Link>
    </div>
  );
}

export default Results;
