import React from 'react';

function EmailList({ emails, onAnswer, responses }) {
  return (
    <ul style={{ listStyle: 'none', padding: 0 }}>
      {emails.map(email => {
        const response = responses.find(r => r.emailId === email.id);
        return (
          <li
            key={email.id}
            style={{
              border: '1px solid #ccc',
              padding: '10px',
              marginBottom: '10px'
            }}
          >
            <p><strong>De:</strong> {email.sender}</p>
            <p><strong>Asunto:</strong> {email.subject}</p>
            <p>{email.body}</p>
            {!response ? (
              <div>
                <button
                  onClick={() => onAnswer(email.id, true)}
                  style={{ marginRight: '10px' }}
                >
                  Phishing
                </button>
                <button onClick={() => onAnswer(email.id, false)}>
                  Legítimo
                </button>
              </div>
            ) : (
              <p style={{ color: response.isCorrect ? 'green' : 'red' }}>
                {response.isCorrect ? '¡Correcto!' : 'Incorrecto'} - {response.explanation}
              </p>
            )}
          </li>
        );
      })}
    </ul>
  );
}

export default EmailList;
